function run(response) {
    let repositorios = JSON.parse(response);

    if (repositorios || repositorios.length === 0) {
        return { "error": "Nenhum repositório encontrado" };
    }

    let carrossel = repositorios.map(repo => ({
        header: {
            type: "application/vnd.lime.media-link+json",
            value: {
                title: repo.nome,
                text: repo.descricao || "Sem descrição disponível",
                type: "image/jpeg",
                uri: repo.imagem || "https://via.placeholder.com/300" 
            }
        }
    }));

    return { 
        "itemType": "application/vnd.lime.document-select+json", 
        "items": carrossel 
    };
}
